            }
        };
    });

To set all  responses to success for a specific request path:


services.AddSingleton<ITelemetryInitializer>(
    (provider) =>
    {
        return new ConflictStatusCodeRequestTelemetryInitializer(provider)
        {
            Requests = RequestTelemetryInitializerBehavior.Specified,
            RequestPaths = new List<string>()
            {
                "/api/",
            }
        };
    });

